mapper.select:

SELECT *
FROM (select  rownum rm,a .* from (select pay.payid,pay.pid,pay.paycount,pay.payaddr,pay.paytel,pay.paydate,pay.paycontent,pay.paytotal,pay.paystate,s.sname from pay pay , store s, product p where pay.username=#{id} and pay.pid =p.pid and  s.sid=p.sid  order by pay.paydate desc) a ) pay
where pay.rm between  #{first} and #{end}
