mapper.select:

SELECT *
FROM (select rownum rm, notice.* from notice where mkId=${mkId}) notice
where rm between ${first} and ${end}

mapper.insert:
INSERT INTO notice
(nId, userName, nTitle, nContent, nDate,delYn,mkId)
VALUES(
seq_nId.nextval,#{userName},#{nTitle},#{nContent},sysdate,'N',#{mkId}
)
